import{_ as h,a as g,H as a,k as t,f as r,R as o,C as n,o as u}from"./chunks/framework.DaaYPR27.js";const m="/diagram/cour.svg",c="/diagram/anipro.svg",p="/glossary/anime/akb.webp",f="/glossary/anime/sk.webp",b="/diagram/genga.svg",y="/glossary/anime/solo.webp",_="/glossary/anime/time.webp",w="/diagram/anifile.svg",N=JSON.parse('{"title":"Anime Glossary","description":"","frontmatter":{"title":"Anime Glossary","customDescription":"Discover the anime related terms you should know!","og":{"image":"https://files.catbox.moe/ys1mv5.png"}},"headers":[],"relativePath":"glossary/anime.md","filePath":"glossary/anime.md","lastUpdated":1750395686000}'),k={name:"glossary/anime.md"},T={id:"basic-terms",tabindex:"-1"},v={id:"release-terms",tabindex:"-1"},S={id:"season",tabindex:"-1"},A={id:"episode-sections",tabindex:"-1"},x={id:"technical-terms",tabindex:"-1"},P={id:"file-naming",tabindex:"-1"},C={id:"subtitle-types",tabindex:"-1"};function E(D,e,V,q,I,O){const s=n("NolebasePageProperties"),l=n("GradientCard"),i=n("Feedback"),d=n("NolebaseGitChangelog");return u(),g("div",null,[a(s),a(l,{title:"Anime Glossary",description:"Anime portion of the Wotaku glossary!",theme:"turquoise",variant:"thin"}),t("h2",T,[e[0]||(e[0]=r("Basic Terms ")),a(i,{heading:"Basic Terms"}),e[1]||(e[1]=t("a",{class:"header-anchor",href:"#basic-terms","aria-label":'Permalink to "Basic Terms"'},"​",-1))]),e[14]||(e[14]=o('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Anison</td><td>The abbreviated form of Anime Song. It indicates the songs present in the Anime. Generally the opening (OP) and ending (ED). More info <a href="#episode-sections">here</a></td></tr><tr><td>Anime Original</td><td>Content that was not present in the original source material. Can be canon or filler.</td></tr><tr><td>Canon</td><td>Canon refers to content that was not present in the original source material but is part of the anime storyline. Canon is used to expand the world-building of the anime, fix the pacing of the source material, or more character development.</td></tr><tr><td>Filler</td><td>Filler refers to content that was not present in the original source material &amp; doesn&#39;t affect the storyline. During filler, the story doesn&#39;t get any progression.</td></tr><tr><td>Original Anime</td><td>Anime that&#39;s not an adaptation of existing material such as Manga, Light novels, picture books, etc.</td></tr><tr><td>PV</td><td>Promotional videos of the anime. Generally teaser, trailer, preview, etc.</td></tr><tr><td>Seiyuu / VA</td><td>The voice actor/actress of the anime.</td></tr></tbody></table>',1)),t("h2",v,[e[2]||(e[2]=r("Release terms ")),a(i,{heading:"Release terms"}),e[3]||(e[3]=t("a",{class:"header-anchor",href:"#release-terms","aria-label":'Permalink to "Release terms"'},"​",-1))]),e[15]||(e[15]=o('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>OAD</td><td>Original Animation DVD. OVAs that were bundled with the source material (e.g. manga).</td></tr><tr><td>OAV</td><td>Original Animation Video. Another term for OVA.</td></tr><tr><td>ONA</td><td>Original Net Animation. Anime that was originally released on an OTT platform.</td></tr><tr><td>OVA</td><td>Original Video Animation. Anime that was originally released on a physical medium.</td></tr><tr><td>Simulcast</td><td>Simultaneous broadcast or stream of the latest episodes alongside their country of origin.</td></tr><tr><td>Simuldub</td><td>Simultaneous dub release of the latest episodes alongside their country of origin.</td></tr></tbody></table>',1)),t("h2",S,[e[4]||(e[4]=r("Season ")),a(i,{heading:"Season"}),e[5]||(e[5]=t("a",{class:"header-anchor",href:"#season","aria-label":'Permalink to "Season"'},"​",-1))]),e[16]||(e[16]=o('<figure><img src="'+m+'" alt="Different types of anime by cour" tabindex="0" loading="lazy"><figcaption>Different types of anime by cour</figcaption></figure><table tabindex="0"><thead><tr><th>Term</th><th>Description</th></tr></thead><tbody><tr><td>Season</td><td>In Japanese TV, the broadcast schedule is divided into four seasons: Spring, Summer, Autumn, and Winter.</td></tr><tr><td>Cour</td><td>Each 13eps/season is called a cour.</td></tr><tr><td>Consecutive-cour</td><td>When the anime season spreads through 2 or more continuously without a seasonal break. Example: 2-cour anime (<a href="https://anilist.co/anime/101922/Kimetsu-no-Yaiba" target="_blank" rel="noreferrer">Kimetsu no Yaiba</a>, <a href="https://anilist.co/anime/176301/Kusuriya-no-Hitorigoto-2nd-Season" target="_blank" rel="noreferrer">The Apothecary Diaries Season 2</a>), 3-cour (<a href="https://anilist.co/anime/1535/DEATH-NOTE" target="_blank" rel="noreferrer">DEATH NOTE</a>) or a 4-cour (<a href="https://anilist.co/anime/20/NARUTO" target="_blank" rel="noreferrer">Naruto</a>)</td></tr><tr><td>Split-cour</td><td>When the anime season isn&#39;t continuous and has a break of one or two cours. Example: <a href="https://anilist.co/anime/142838/SPYFAMILY-Part-2" target="_blank" rel="noreferrer">Spy x Family</a></td></tr></tbody></table>',2)),t("h2",A,[e[6]||(e[6]=r("Episode Sections ")),a(i,{heading:"Episode Sections"}),e[7]||(e[7]=t("a",{class:"header-anchor",href:"#episode-sections","aria-label":'Permalink to "Episode Sections"'},"​",-1))]),e[17]||(e[17]=o('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Branding</td><td>The logo animation of studios and the distributor of the anime are shown here. The topic is really well-explained by <a href="https://youtu.be/m29FhW1e3_g" target="_blank" rel="noreferrer"><strong>Channel 45</strong></a></td></tr><tr><td>Recap</td><td>Recaps the previous episode(s). Generally it&#39;s found in long-running shōnen series. <a href="https://www.youtube.com/watch?v=R8jVqCKt4ZI" target="_blank" rel="noreferrer">Example</a></td></tr><tr><td>Cold Opening</td><td>It&#39;s the portion of the anime before the OP. Sometimes it can be just a recap or a setup for the episode.</td></tr><tr><td>OP</td><td>Opening sequence of the anime, generally 1.5 minutes long. Depending on the episode they can also be placed closer to the middle or at the very end (example: Solo leveling s01e01). The scenes shown in them can be made for the OP only and not appear in the actual show.</td></tr><tr><td>Title Card</td><td>Also known as Title sequence. It is a part of the OP. <a href="https://youtu.be/QoGM9hCxr4k?t=19" target="_blank" rel="noreferrer">Example</a></td></tr><tr><td>Mixed OP</td><td>When the OP is overlaid with the plot of the anime.</td></tr><tr><td>Eyecatch</td><td>The animated sequence before the ad break or the end of the first part. It&#39;s generally a still frame. <a href="https://www.youtube.com/watch?v=vIZf2qfXeSI" target="_blank" rel="noreferrer">Example</a></td></tr><tr><td>Insert song</td><td>A song or musical piece that occurs within the body of the anime. <a href="https://www.youtube.com/watch?v=1_PWdjym25g" target="_blank" rel="noreferrer">Example</a></td></tr><tr><td>ED</td><td>The ending sequence of the anime.</td></tr><tr><td>Mixed ED</td><td>Same as Mixed OP but for ED. <a href="https://www.youtube.com/watch?v=2ikL1vGhMLw" target="_blank" rel="noreferrer">Chainsaw-man Ep 12</a> &amp; <a href="https://animethemes.moe/anime/relife/ED13-NCBD1080" target="_blank" rel="noreferrer">Relife Ep 13</a></td></tr><tr><td>Omake</td><td>An extra portion of the episode. Generally a filler. <a href="https://www.youtube.com/watch?v=Dtu_1bzSmL8" target="_blank" rel="noreferrer">Example</a></td></tr><tr><td>Preview</td><td>Shows a glimpse of the next episode which is generally an action sequence. This is also used as PV.</td></tr></tbody></table>',1)),t("h2",x,[e[8]||(e[8]=r("Technical terms ")),a(i,{heading:"Technical terms"}),e[9]||(e[9]=t("a",{class:"header-anchor",href:"#technical-terms","aria-label":'Permalink to "Technical terms"'},"​",-1))]),e[18]||(e[18]=t("h3",{id:"anime-production-process",tabindex:"-1"},[r("Anime Production Process "),t("a",{class:"header-anchor",href:"#anime-production-process","aria-label":'Permalink to "Anime Production Process"'},"​")],-1)),e[19]||(e[19]=t("figure",null,[t("img",{src:c,alt:"Edited version of flowchart by Dong Chang",tabindex:"0",loading:"lazy"}),t("figcaption",null,"Edited version of flowchart by Dong Chang")],-1)),e[20]||(e[20]=t("div",{class:"video_wrapper"},[t("iframe",{src:"https://www.youtube.com/embed/D0tux0QoeUE",frameborder:"0",allowfullscreen:""})],-1)),e[21]||(e[21]=o('<div class="tip custom-block"><p class="custom-block-title">Extra</p><p>Mentioned <a href="https://wiki.evageeks.org/Resources:Neon_Genesis_Evangelion_Proposal_(Translation)#Page_2_(Synopsis_and_Staff)" target="_blank" rel="noreferrer">Kikakusho / Pitch package</a> of Neon Genesis Evangelion.</p></div><h3 id="settai" tabindex="-1">Settai <a class="header-anchor" href="#settai" aria-label="Permalink to &quot;Settai&quot;">​</a></h3><p>Settai is collection of model sheets and important places (can be a house, place or anything important). The designs go through multiple stages, so they can change over time. The final drawings are used by staff as the character standard. Depending on the budget, settai can have very high details.</p><figure><img src="'+p+'" alt="Akebi-chan no Sailor-fuku" tabindex="0" loading="lazy"><figcaption>Akebi-chan no Sailor-fuku</figcaption></figure><div class="tip custom-block"><p class="custom-block-title">Extra</p><p><a href="https://setteidreams.net/" target="_blank" rel="noreferrer">Settai Dreams</a> &amp; <a href="https://characterdesignreferences.com/" target="_blank" rel="noreferrer">Character Design References</a> have a lot of model sheets. Visit these sites for more examples.</p></div><h3 id="e-konte" tabindex="-1">E-Konte <a class="header-anchor" href="#e-konte" aria-label="Permalink to &quot;E-Konte&quot;">​</a></h3><p>E-Konte / storyboard shows all the sequences in rough sketches so the director can show the vision to the animators and organize accordingly. It generally has 5 columns:</p><ol><li>Scene &amp; cut number</li><li>Scene rough drawing</li><li>Animation instructions</li><li>Dialogue &amp; SFX</li><li>Time &amp; frame number</li></ol><p>Formatting of a storyboard can change depending on the studio and director.</p><figure><img src="'+f+'" alt="Shaman King (2021) Opening #02" tabindex="0" loading="lazy"><figcaption>Shaman King (2021) Opening #02</figcaption></figure>',10)),e[22]||(e[22]=t("div",{class:"video_wrapper"},[t("iframe",{src:"https://www.youtube.com/embed/ln1sKXXpSdg",frameborder:"0",allowfullscreen:""})],-1)),e[23]||(e[23]=o('<div class="tip custom-block"><p class="custom-block-title">Extra</p><p><a href="https://x.com/guyayaHime" target="_blank" rel="noreferrer">rinri</a> posts a lot of stuff regarding storyboard. You can check out her posts there.</p></div><h3 id="genga" tabindex="-1">Genga <a class="header-anchor" href="#genga" aria-label="Permalink to &quot;Genga&quot;">​</a></h3><figure><img src="'+b+'" alt="Genga workflow" tabindex="0" loading="lazy"><figcaption>Genga workflow</figcaption></figure><p>Genga means tie-down drawings or raw keyframes, drawn by genga artists. The drawn genga goes though a multi-step process.</p><p>Genga generally goes for important details of a scene. It can be just the character (or part of the character) or the character and their surrounding if the character is interacting with it. Genga doesn&#39;t generally include frame in-betweens, so it will be rough in motion by nature.</p>',5)),e[24]||(e[24]=t("div",{class:"video_wrapper"},[t("iframe",{src:"https://www.youtube.com/embed/JM-AFeBtvA4",frameborder:"0",allowfullscreen:""})],-1)),e[25]||(e[25]=o('<details class="details custom-block"><summary>Short note</summary><p>If you look at the sakuga, we can see a few numbers.</p><ul><li><strong>160</strong> -&gt; Scene number</li><li><strong>01</strong> -&gt; Time (in seconds) of that scene</li><li><strong>16</strong> -&gt; Shot number of that scene</li></ul><figure><img src="'+y+'" alt="Solo Leveling ep. 11" tabindex="0" loading="lazy"><figcaption>Solo Leveling ep. 11</figcaption></figure></details><div class="tip custom-block"><p class="custom-block-title">Extra</p><p>We tried to really simplify the process. For more details, check out <a href="https://www.youtube.com/@DongChang" target="_blank" rel="noreferrer"><strong>Dong Chang</strong></a>, <a href="https://www.youtube.com/@SakugaFoundry" target="_blank" rel="noreferrer"><strong>Sakuga Foundry</strong></a> &amp; <a href="https://www.youtube.com/@animator" target="_blank" rel="noreferrer">Iwane Masaaki’s Animating Channel</a>.</p></div><h3 id="timesheet" tabindex="-1">Timesheet <a class="header-anchor" href="#timesheet" aria-label="Permalink to &quot;Timesheet&quot;">​</a></h3><figure><img src="'+_+'" alt="Satsuma! by Miyakawa (Unofficial TL by Liss)" tabindex="0" loading="lazy"><figcaption>Satsuma! by Miyakawa (Unofficial TL by Liss)</figcaption></figure><div class="tip custom-block"><p class="custom-block-title">Extra</p><p><a href="https://satuma.grupo.jp/free838189" target="_blank" rel="noreferrer"><strong>Free PDF book</strong></a> by <a href="https://x.com/satuma_info" target="_blank" rel="noreferrer"><strong>Miyakawa</strong></a> contains a lot of info about timesheets / exposure sheets. <a href="https://x.com/LissBevs" target="_blank" rel="noreferrer"><strong>Liss</strong></a> has been doing an unofficial English translation of the book. You can find a partial compiled version <a href="https://drive.google.com/file/d/1yB2U-OONyAHhFnWsNJhU-R-5-t26YwlY/view" target="_blank" rel="noreferrer"><strong>here</strong></a> and for the latest updates, <a href="https://x.com/hashtag/satsuma_pdf?f=live" target="_blank" rel="noreferrer"><strong>click here</strong></a>.</p></div><h3 id="douga" tabindex="-1">Douga <a class="header-anchor" href="#douga" aria-label="Permalink to &quot;Douga&quot;">​</a></h3><p>More cleaned up frames, done by key animators. Dougas are way more completed and have more shading in general. The final animation looks pretty close to douga. In-betweens are also added in this step. Sometime, genga and douga can look pretty close depending on the artist.</p>',7)),t("h2",P,[e[10]||(e[10]=r("File Naming ")),a(i,{heading:"File Naming"}),e[11]||(e[11]=t("a",{class:"header-anchor",href:"#file-naming","aria-label":'Permalink to "File Naming"'},"​",-1))]),e[26]||(e[26]=o('<figure><img src="'+w+'" alt="Anime file name sections" tabindex="0" loading="lazy"><figcaption>Anime file name sections</figcaption></figure><h3 id="release-group" tabindex="-1">Release group <a class="header-anchor" href="#release-group" aria-label="Permalink to &quot;Release group&quot;">​</a></h3><p>The individual or team behind the release, also called Encoder in the case of encodes. Example: SubsPlease, Erai-raws, EMBER, Yameii, etc. Sometimes, the release group name is mentioned at the end.</p><h3 id="source" tabindex="-1">Source <a class="header-anchor" href="#source" aria-label="Permalink to &quot;Source&quot;">​</a></h3><p>The origin of the release, so streaming sites or different disc releases (DVD, LaserDisc, Blu-ray, etc.).</p><ul><li><strong>WEB-DL</strong>: Untouched videos, downloaded from streaming sites without encoding</li><li><strong>WEB-Rip</strong>: Videos sourced from streaming sites that went through screen-capture and/or encoding methods</li><li><strong>BDISO</strong>: A disk image of a Blu-ray disc, needs to be mounted in order to be played (like other ISOs) and is used for making encodes or remuxes.</li><li><strong>BDMV</strong>: Short for “Blu-ray Disc Movie”, a container format used to store high-definition video, audio, and other related content on Blu-ray discs.</li><li><strong>Remux</strong>: Videos sourced from disc releases without encoding, making them have very high quality and file sizes.</li><li><strong>Encode</strong>: A video that has gone through lossless or lossy compression processes in order to save file size, also called a DVDRip or BDRip.</li><li><strong>Re-Encode</strong>: An encode that has gone through the process of further encoding, thus degrading in quality.</li><li><strong>Mini-Encode</strong>: A video where the priority is the smallest possible file size, even if the quality would greatly suffer as a result.</li></ul><h3 id="resolution" tabindex="-1">Resolution <a class="header-anchor" href="#resolution" aria-label="Permalink to &quot;Resolution&quot;">​</a></h3><p>The number of pixels contained in each frame, a higher value means improved quality (unless it&#39;s an upscaled release). The most common ones are 360p, 480p, 576p, 720p, 1080p, and 2160p. Most anime nowadays are made in 1080p. So, most of the 2160p anime are interpolated. This interpolation can cause artifacts, over sharpening, or aggressive denoising since it&#39;s not really possible to add details out of nothing.</p><h3 id="video-codec" tabindex="-1">Video Codec <a class="header-anchor" href="#video-codec" aria-label="Permalink to &quot;Video Codec&quot;">​</a></h3><p>The encoding process for the video file. The most popular encoders today are <strong>x264</strong> (AVC) and <strong>x265</strong> (HEVC), with the latter being newer and able to achieve higher compression rates with less quality loss.</p><div class="tip custom-block"><p class="custom-block-title">Extra</p><p>To learn more about encoding, visit <a href="https://wiki.x266.mov/docs/introduction/prologue" target="_blank" rel="noreferrer"><strong>Codec Wiki</strong></a>.</p></div><h3 id="audio-format" tabindex="-1">Audio Format <a class="header-anchor" href="#audio-format" aria-label="Permalink to &quot;Audio Format&quot;">​</a></h3><p>Used in the encoding process for encoding audio, divided into lossless and lossy formats.</p><ul><li><strong>Lossless</strong>: These retain the highest audio quality and have larger file sizes as a consequence. The most common one is FLAC.</li><li><strong>Lossy</strong>: These formats aim to provide good quality while keeping file sizes small. The most popular ones today are AAC, AC3, EAC3 (all commonly found in Web releases), with MP3 being found in older releases.</li></ul><h3 id="subtitles" tabindex="-1">Subtitles <a class="header-anchor" href="#subtitles" aria-label="Permalink to &quot;Subtitles&quot;">​</a></h3><ul><li><strong>Raw</strong>: A video without subtitles.</li><li><strong>Fansubs</strong>: Unofficial aka fan-made translations or an edited version of the official subtitle.</li><li><strong>Hardsubs</strong>: Subtitles that are burned into the frames and are part of the video track itself, can&#39;t be turned off.</li><li><strong>Softsubs</strong>: Subtitles that are contained on their own track inside a video file or a separate file that&#39;s loaded with the video, can be turned on or off.</li></ul><h3 id="file-hash" tabindex="-1">File Hash <a class="header-anchor" href="#file-hash" aria-label="Permalink to &quot;File Hash&quot;">​</a></h3><p>These are <a href="https://en.wikipedia.org/wiki/Cyclic_redundancy_check" target="_blank" rel="noreferrer">CRC32</a> codes, used to detect errors during file transmission. For example: 7BAAC64C. CRC generates a 32-bit code for each file (in hexadecimal, it&#39;s 8 digits). Fansubbers include these codes in the filename so that users can easily <a href="https://animechecker.sourceforge.net/" target="_blank" rel="noreferrer">check</a> whether the downloaded file is corrupted or not. If the file is corrupted, the user will receive a different code than the one written in the file name.</p><h3 id="video-format" tabindex="-1">Video Format <a class="header-anchor" href="#video-format" aria-label="Permalink to &quot;Video Format&quot;">​</a></h3><p>The container used for the video file, with the most widely used one today being <strong>MKV</strong>, for the ability to put multiple audio/subtitle tracks in one video file. The most popular one in the past was <strong>MP4</strong>, which focused on being compatible with a wide range of devices, but was only able to have burned-in hardsubs.</p>',20)),t("h2",C,[e[12]||(e[12]=r("Subtitle types ")),a(i,{heading:"Subtitle types"}),e[13]||(e[13]=t("a",{class:"header-anchor",href:"#subtitle-types","aria-label":'Permalink to "Subtitle types"'},"​",-1))]),e[27]||(e[27]=o('<table tabindex="0"><thead><tr><th>Type</th><th>Description</th></tr></thead><tbody><tr><td>Closed Captions (CC)</td><td>Also known as soft subtitles; text overlays on a video that provide translated dialogue or just audio transcription in case they&#39;re following the dub; can be turned off if not needed.</td></tr><tr><td>Open Captions (OC)</td><td>Also known as burned-in/hard subtitles; non-interactive subtitles that have been permanently &#39;burned&#39; into the video file itself and can&#39;t be turned off.</td></tr><tr><td>SDH (Subtitles for the Deaf and Hard of Hearing)</td><td>A type of subtitle that includes not only dialogue but also annotations for sound effects and other audio information to aid viewers who are deaf or hard of hearing.</td></tr><tr><td>Forced Subtitles</td><td>Subtitles that automatically appear when a portion of dialogue or text is in a different language from the main audio track; they are &#39;forced&#39; on screen to provide necessary translation or context.</td></tr><tr><td>Karaoke Subtitles</td><td>Karaoke subtitles highlight the lyrics in time with the Opening/Ending/Insert songs.</td></tr></tbody></table>',1)),a(d)])}const F=h(k,[["render",E]]);export{N as __pageData,F as default};
