import{_ as h,a as m,H as a,k as t,f as o,R as i,C as s,o as c}from"./chunks/framework.DaaYPR27.js";const u="/glossary/general/bakadare.webp",g="/glossary/general/byoukidere.webp",p="/glossary/general/dandere.webp",f="/glossary/general/deredere.webp",y="/glossary/general/goudere.webp",b="/glossary/general/hajidere.webp",k="/glossary/general/hinedere.webp",w="/glossary/general/hiyakasudere.webp",_="/glossary/general/kanedere.webp",v="/glossary/general/mayadere.webp",T="/glossary/general/nemuidere.webp",x="/glossary/general/nyandere.webp",S="/glossary/general/sadodere.webp",A="/glossary/general/kekkondere.webp",E="/glossary/general/kuudere.webp",D="/glossary/general/yandere.webp",M="/glossary/general/yottadere.webp",Y=JSON.parse('{"title":"General Glossary","description":"","frontmatter":{"title":"General Glossary","customDescription":"Weeb terms that you will see everywhere!","og":{"image":"https://files.catbox.moe/ys1mv5.png"}},"headers":[],"relativePath":"glossary/general.md","filePath":"glossary/general.md","lastUpdated":1750395686000}'),C={name:"glossary/general.md"},I={id:"region",tabindex:"-1"},j={id:"demography",tabindex:"-1"},N={id:"genre",tabindex:"-1"},K={id:"terms",tabindex:"-1"},z={id:"common-honorifics",tabindex:"-1"},H={id:"doujin-basics",tabindex:"-1"},V={id:"idol-vtuber-terms",tabindex:"-1"},P={id:"kemonomimi",tabindex:"-1"},q={id:"dere-types",tabindex:"-1"};function G(R,e,B,L,F,W){const n=s("NolebasePageProperties"),d=s("GradientCard"),r=s("Feedback"),l=s("NolebaseGitChangelog");return c(),m("div",null,[a(n),a(d,{title:"Wotaku Glossary",description:"Want to increase your otaku knowledge? Look for no further!",theme:"turquoise",variant:"thin"}),e[18]||(e[18]=t("br",null,null,-1)),t("h2",I,[e[0]||(e[0]=o("Region ")),a(r,{heading:"Region"}),e[1]||(e[1]=t("a",{class:"header-anchor",href:"#region","aria-label":'Permalink to "Region"'},"​",-1))]),e[19]||(e[19]=i('<table tabindex="0"><thead><tr><th style="text-align:center;">Country</th><th style="text-align:center;">Comics</th><th style="text-align:center;" class="compact">Animation</th></tr></thead><tbody><tr><td style="text-align:center;">Japan</td><td style="text-align:center;">Manga</td><td style="text-align:center;">Anime</td></tr><tr><td style="text-align:center;">Korea</td><td style="text-align:center;">Manhwa</td><td style="text-align:center;">Aeni</td></tr><tr><td style="text-align:center;">China</td><td style="text-align:center;">Manhua</td><td style="text-align:center;">Donghua</td></tr></tbody></table>',1)),t("h2",j,[e[2]||(e[2]=o("Demography ")),a(r,{heading:"Demography"}),e[3]||(e[3]=t("a",{class:"header-anchor",href:"#demography","aria-label":'Permalink to "Demography"'},"​",-1))]),e[20]||(e[20]=i('<table tabindex="0"><thead><tr><th>Genre</th><th>Age range</th><th>Target audience</th></tr></thead><tbody><tr><td>Kodomo</td><td>under 12</td><td>Kids</td></tr><tr><td>Shounen</td><td rowspan="2">12-18</td><td>Boys</td></tr><tr><td>Shoujo</td><td>Girls</td></tr><tr><td>Seinen</td><td rowspan="2">19-40+</td><td>Men</td></tr><tr><td>Josei</td><td>Women</td></tr></tbody></table><p>Demography refers to the target audience for a work. While studios / publisher market content toward this group, it can still appeal to multiple demographics, as many anime and manga include elements for a broad audience.</p><div class="tip custom-block"><p class="custom-block-title">Extra</p><p><a href="/glossary/manga#magazine">Click here</a> to browse popular manga magazines by genre.</p></div>',3)),t("h2",N,[e[4]||(e[4]=o("Genre ")),a(r,{heading:"Genre"}),e[5]||(e[5]=t("a",{class:"header-anchor",href:"#genre","aria-label":'Permalink to "Genre"'},"​",-1))]),e[21]||(e[21]=i('<table tabindex="0"><thead><tr><th>Genre</th><th class="compact">Description</th></tr></thead><tbody><tr><td>Ecchi</td><td>Anime or manga with lewd scenes but is not necessarily explicit or pornographic. Example: <a href="https://anilist.co/anime/11617/High-School-DxD/" target="_blank" rel="noreferrer">High School DxD</a></td></tr><tr><td>Isekai</td><td>Type of story where MC is transported to another world. Example: <a href="https://anilist.co/anime/108465/Mushoku-Tensei-Jobless-Reincarnation/" target="_blank" rel="noreferrer">Mushoku Tensei</a></td></tr><tr><td>Mahou Shoujo</td><td>Literally &#39;Magical Girl&#39;, subgenre of fantasy centered around young girls with magical abilities. Example: <a href="https://anilist.co/anime/530/Sailor-Moon/" target="_blank" rel="noreferrer">Sailor Moon</a></td></tr><tr><td>Mecha</td><td>Abbreviation for &#39;Mechanical&#39;, shows heavily feature or focus on mechanical innovation. Example: <a href="https://anilist.co/anime/30/Shin-Seiki-Evangelion" target="_blank" rel="noreferrer">Neon Genesis Evangelion</a></td></tr><tr><td>Shoujo-ai</td><td>Anime and manga exploring emotional, platonic, homoerotic relationships between women. <em>Note: Explicit content is generally referred to as &#39;Yuri&#39; or &#39;GL&#39;.</em></td></tr><tr><td>Shounen-ai</td><td>Anime and manga exploring emotional, platonic, homoerotic relationships between men. <em>Note: Explicit content is generally referred to as &#39;Yaoi&#39; or &#39;BL&#39;.</em></td></tr><tr><td>Slice of Life</td><td>Shows that focus on the day-to-day lives of fictional characters, offering chill experiences. Example: <a href="https://anilist.co/anime/10165/Nichijou" target="_blank" rel="noreferrer">Nichijou</a></td></tr><tr><td>Wuxia</td><td>Literally means “Martial Heroes”. Fictional stories about humans with supernatural fighting ability. Themes of chivalry, tragedy, revenge &amp; romance are common.</td></tr></tbody></table>',1)),t("h2",K,[e[6]||(e[6]=o("Terms ")),a(r,{heading:"Terms"}),e[7]||(e[7]=t("a",{class:"header-anchor",href:"#terms","aria-label":'Permalink to "Terms"'},"​",-1))]),e[22]||(e[22]=i('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Ahoge</td><td>Literally &#39;Stupid Hair&#39;. refers to a strand of hair that springs upwards from the character&#39;s head. Example: <a href="https://anidb.net/character/2999" target="_blank" rel="noreferrer">Izumi Konata</a></td></tr><tr><td>Baka</td><td>Stupid.</td></tr><tr><td>Chibi</td><td>Literally &quot;Small/Short&quot;. In real life it can be insulting but in anime it&#39;s used for cutesy style. Example: <a href="https://anidb.net/character/75170" target="_blank" rel="noreferrer">Doma Umaru</a></td></tr><tr><td>Chuunibyou</td><td><em>It&#39;s not possible to make a justified explanation without oversimplifying things. So, read <a href="https://rentry.org/whatisChuunibyou" target="_blank" rel="noreferrer">this post</a> instead.</em></td></tr><tr><td>Doujinka / Doujinshika</td><td>Doujinka is the creator of doujinshi and is a member of a doujin circle.</td></tr><tr><td>Doujinshi</td><td>Doujinshi refers to self-published works, which can be original creations or fan fictions, such as manga, novels, artworks, music, and games.</td></tr><tr><td>Ecchi</td><td>Lewd.</td></tr><tr><td>Fujoshi</td><td>&#39;Rotten Girl&#39;. Refers to a girl that fantasizes about guys being in a relationship with other guys, shipping male characters with other male characters in manga and anime.</td></tr><tr><td>Fudanshi</td><td>&#39;Rotten Boy&#39;, the male equivalent of a fujoshi.</td></tr><tr><td>Gijinka</td><td>A type of fan-art or cosplay in which a non-human character is depicted in a human-like way (akin to personification)</td></tr><tr><td>Gravure idol</td><td>Gravure idols are models who pose in provocative swimsuit and lingerie photographs in magazines and photo books marketed towards teens (often in shonen magazine), men, similar to pin-up models.</td></tr><tr><td>Gyaru</td><td>English slang equivalent of &quot;Gal&quot;. It refers to a subculture and fashion style in Japan where girls wear dramatic tan makeup, sport big hair, and embrace 2000s fashion trends. Example: <a href="https://anidb.net/character/120646" target="_blank" rel="noreferrer">Kitagawa Marin</a></td></tr><tr><td>Hentai</td><td>A term literally meaning pervert that is also used as a synonym for both animated and drawn pornography</td></tr><tr><td>Hikikomori</td><td>Shut-ins, people who have cut themselves off from society and don&#39;t leave their homes, or even their rooms in the most extreme cases. The word Hikikomori can be used both for the phenomenon and the recluses themselves.</td></tr><tr><td>JK</td><td>&quot;Joshi Kousei&quot;. Meaning high-school girl.</td></tr><tr><td>Kaiju</td><td>Giant monsters. It can be mentioned as a genre too.</td></tr><tr><td>Kawaii</td><td>Adorable.</td></tr><tr><td>Kouhai</td><td>Junior. Can be in a workplace or school.</td></tr><tr><td>Loli</td><td>Young or young-looking girl in anime and manga.</td></tr><tr><td>Moe</td><td>The feeling of emotional attraction when you see something cute in manga, anime or other weeb stuff. These are SFW and can be any gender.</td></tr><tr><td>Nakama</td><td>Literally &quot;Comrade&quot;. Popularized by the series &#39;One Piece&#39;, it is nowadays used for a group of people who are closer than family.</td></tr><tr><td>Seiso</td><td>&quot;Seiso&quot; (清楚) refers to a particular aesthetic of purity and cleanliness that is often emphasized in the presentation of female idols. This can involve elements such as modest clothing, subtle makeup, and a generally wholesome image.</td></tr><tr><td>Senpai / Sempai</td><td>Senior. Can be in a workplace or school.</td></tr><tr><td>Sensei</td><td>Generally Teacher. But this can also be used to address someone who is a respected professional or very senior in the position.</td></tr><tr><td>Shikishi</td><td>It is a high-quality art board mainly used for calligraphy and painting. Due to its exceptional quality, it is often utilized for collecting autographs featuring both simple and highly detailed artwork. Additionally, it is sometimes used as a promotional material for the show. <a href="https://old.reddit.com/r/Shikishi/" target="_blank" rel="noreferrer">Examples of shikishi</a>.</td></tr><tr><td>Shota</td><td>Young or young-looking boy in anime and manga.</td></tr><tr><td>Tokusatsu</td><td>Literally &quot;Special effects&quot;. This term can be applied to movies and tv shows with superheroes with a recognizable style of special effects. <a href="https://www.youtube.com/watch?v=bBsDwQ1by_o" target="_blank" rel="noreferrer">Examples of Tokusatsu</a></td></tr><tr><td>Trap</td><td>A word used in the anime community to refer to a person who looks the opposite gender. Most commonly used for male characters who look female. Example: <a href="https://anidb.net/character/23270" target="_blank" rel="noreferrer">Urushibara Ruka from Steins; Gate</a></td></tr><tr><td>Yamato Nadeshiko</td><td>A Japanese term used to describe an ideal Japanese woman. They are the epitome of pure, feminine beauty.</td></tr><tr><td>Zettai Ryouiki</td><td>Translates to &#39;Absolute Territory&#39;. Refers to the space of bare thighs between the skirt and above knee-length socks.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">More words</p><p>More terms can be found in <a href="https://www.wikipedia.org/" target="_blank" rel="noreferrer">Wikipedia</a>, <a href="https://dic.pixiv.net/" target="_blank" rel="noreferrer">pixiv Encyclopedia</a> &amp; <a href="https://www.fandom.com/" target="_blank" rel="noreferrer">Fandom</a>.</p></div>',2)),t("h2",z,[e[8]||(e[8]=o("Common Honorifics ")),a(r,{heading:"Common Honorifics"}),e[9]||(e[9]=t("a",{class:"header-anchor",href:"#common-honorifics","aria-label":'Permalink to "Common Honorifics"'},"​",-1))]),e[23]||(e[23]=i('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>-san</td><td>It is used after a person&#39;s name to show respect. It is equivalent to &#39;Mr.&#39;, &#39;Mrs.&#39;, or &#39;Miss&#39; in English.</td></tr><tr><td>-sama</td><td>Formal version of -san, used to address a person of higher rank/importance, also commonly used by employees when addressing customers.</td></tr><tr><td>-kun</td><td>Used most often when referring to boys, this indicates affection or familiarity. Although it may occasionally be used by older men within their social circle, it can also be employed by anyone in reference to someone of lower status.</td></tr><tr><td>-chan</td><td>An affectionate honorific indicating familiarity used mostly in reference to girls; it is also used in reference to cute people or animals of either gender.</td></tr><tr><td>-chin</td><td>Cutesy way of saying chan among girls. Can be sarcastic sometimes, depending on the context.</td></tr><tr><td>-tan</td><td>Generally used for childish-talk among girls. It&#39;s also used in romantic talk (in private scenario), adding <a href="#terms">moe</a>-vibe in their conversations or in a bar where hostess address themselves as -tan.</td></tr><tr><td>-(o)nii-chan</td><td>A familiar, somewhat childish way to refer to one’s older brother.</td></tr><tr><td>-(o)nee-chan</td><td>A familiar, somewhat childish way to refer to one&#39;s older sister.</td></tr><tr><td>-senpai</td><td>It is used to address upperclassmen or more experienced coworkers.</td></tr><tr><td>-sensei</td><td>A respectful term for teachers, artists, or high-level professionals.</td></tr><tr><td>No honorific</td><td>Indicates familiarity or closeness; if used without permission or reason, addressing someone in this manner would constitute as an insult.</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">More words</p><p>If you want to mention a family member in a formal way, here is <a href="https://rentry.org/jpfamily" target="_blank" rel="noreferrer"><strong>the guide</strong></a>.</p></div>',2)),t("h2",H,[e[10]||(e[10]=o("Doujin Basics ")),a(r,{heading:"Doujin Basics"}),e[11]||(e[11]=t("a",{class:"header-anchor",href:"#doujin-basics","aria-label":'Permalink to "Doujin Basics"'},"​",-1))]),e[24]||(e[24]=i('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Doujinshi</td><td>Doujinshi refers to <strong>self-published works</strong>, which can be <strong>original creations or fan fictions</strong>, such as manga, novels, artworks, music, and games.</td></tr><tr><td>Doujinka / Doujinshika</td><td>Doujinka is <strong>the creator</strong> of doujinshi and is a member of a doujin circle.</td></tr><tr><td>Doujin</td><td>&#39;Doujin&#39; refers to <strong>a group</strong> of people who share a common interest.</td></tr><tr><td>Circle</td><td>The term &#39;circle&#39; refers to the group that <strong>releases doujinshi</strong>, the circle may consist of a group of people or a single individual.</td></tr></tbody></table>',1)),t("h2",V,[e[12]||(e[12]=o("Idol / VTuber terms ")),a(r,{heading:"Idol / VTuber terms"}),e[13]||(e[13]=t("a",{class:"header-anchor",href:"#idol-vtuber-terms","aria-label":'Permalink to "Idol / VTuber terms"'},"​",-1))]),e[25]||(e[25]=i('<table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Idol</td><td>Idols are live entertainers who are expected to be &#39;Seiso&#39; or pure, meaning they do not engage in romantic relationships. Their primary goal is to entertain their fans. Idol culture is particularly popular in Asia, especially Japan and Korea. Idols are usually local, but occasionally, they can be foreigners too. <br> However, the idol culture can be dark and questionable, despite being portrayed as glamorous in anime and manga. Idol groups can be a girls or boys group. Example: <a href="https://www.akb48.co.jp/" target="_blank" rel="noreferrer">AKB48</a>. Nowadays, VTuber groups such as <a href="https://www.youtube.com/@hololive" target="_blank" rel="noreferrer">Hololive</a> are managed as an idol group.</td></tr><tr><td>VTuber</td><td>A content creator, typically a streamer, who utilizes a 2D or 3D avatar instead of appearing as themselves. However, it is becoming increasingly common for VTubers to reveal themselves on social media or during streams. This often depends on the policies of the managing agency.</td></tr><tr><td>Oshi</td><td>One&#39;s favourite idol. Also known as bias.</td></tr><tr><td>Gachikoi / Riako</td><td>The fan himself. It&#39;s generally used when the affection is on a serious level.</td></tr><tr><td>Kamioshi</td><td>If someone has more than one oshi, then his/her most favourite oshi is known as kamioshi.</td></tr><tr><td>DD / Daredemo Daisuki</td><td>Literally means &quot;I like everyone&quot;. Used when one likes everyone in the idol group.</td></tr><tr><td>Chika Idol</td><td>Underground Idol.</td></tr><tr><td>Corpo / Corporate VTuber</td><td>A VTuber under contract with an agency is known as a corporate VTuber.</td></tr><tr><td>Indie / Independent VTuber</td><td>If she/he is not under any sort of contract.</td></tr><tr><td>Oshi Mark</td><td>It&#39;s used to indicate an idol or VTuber. It can be based by their name, their VTuber model, inside joke or just random. For example, <a href="https://www.youtube.com/@HoshimachiSuisei" target="_blank" rel="noreferrer"><strong>Hoshimachi Suisei</strong></a>&#39;s oshi mark is <span class="i-twemoji-comet"></span>. It&#39;s because the term &quot;Suisei (彗星)&quot; means comet. <a href="https://www.youtube.com/@NinomaeInanis" target="_blank" rel="noreferrer"><strong>Ninomae Ina&#39;nis</strong></a>&#39;s <span class="i-twemoji-octopus"></span> because of her model.</td></tr><tr><td>Graduation</td><td>When an idol or VTuber bids farewell to their fans. This can happen for various reasons, such as a change in goals, lack of progress, or management issues. <br> Graduation is generally viewed positively. However, if the idol or VTuber breaks any rules, depending on the severity, they may be terminated from the group, which is viewed much more negatively. Many corporate VTubers return to their past persona or adopt a new model to stream again, either as an indie or with a different corporation.</td></tr><tr><td>PL / Past life</td><td>Refers to a VTuber&#39;s previous streaming activities, which can include another VTubing account or IRL streaming. Generally, discussing a VTuber&#39;s past life on stream or in servers is not appreciated, although there are exceptions.&quot;</td></tr></tbody></table>',1)),t("h2",P,[e[14]||(e[14]=o("Kemonomimi ")),a(r,{heading:"Kemonomimi"}),e[15]||(e[15]=t("a",{class:"header-anchor",href:"#kemonomimi","aria-label":'Permalink to "Kemonomimi"'},"​",-1))]),e[26]||(e[26]=i('<p>&quot;Kemonomimi&quot; means &quot;animal ears.&quot; They are human-like characters who possess animal features such as ears, tails etc. Example: <a href="https://anidb.net/character/94007" target="_blank" rel="noreferrer">Toukai Teiou from Uma Musume</a></p><table tabindex="0"><thead><tr><th style="text-align:center;">Types</th><th style="text-align:center;" class="compact">Meaning</th></tr></thead><tbody><tr><td style="text-align:center;">Inumimi</td><td style="text-align:center;">Dog ears</td></tr><tr><td style="text-align:center;">Kitsunemimi</td><td style="text-align:center;">Fox ears</td></tr><tr><td style="text-align:center;">Nekomimi</td><td style="text-align:center;">Cat ears</td></tr><tr><td style="text-align:center;">Nezumimi</td><td style="text-align:center;">Mouse ears</td></tr><tr><td style="text-align:center;">Umamimi</td><td style="text-align:center;">Horse ears</td></tr><tr><td style="text-align:center;">Usagimimi</td><td style="text-align:center;">Rabbit ears</td></tr><tr><td style="text-align:center;">Ryumimi</td><td style="text-align:center;">Dragon ears</td></tr></tbody></table>',2)),t("h2",q,[e[16]||(e[16]=o("Dere Types ")),a(r,{heading:"Dere Types"}),e[17]||(e[17]=t("a",{class:"header-anchor",href:"#dere-types","aria-label":'Permalink to "Dere Types"'},"​",-1))]),e[27]||(e[27]=i('<p>Words used to describe anime girls based on their personality.</p><details class="details custom-block"><summary>Bakadere</summary><p>As clear by the word &quot;baka&quot;, these character(s) are literally idiots, clumsy, and do things that are questionable, but due to their child-like innocence and optimism, they are kind-hearted to other characters, even though they may not realize that, and are very annoying to a protagonist. Example: <a href="https://anidb.net/character/89068" target="_blank" rel="noreferrer">Hanabatake Yoshiko from Aho-Girl</a></p><figure><img src="'+u+'" alt="Hanabatake Yoshiko" tabindex="0" loading="lazy"><figcaption>Hanabatake Yoshiko</figcaption></figure></details><details class="details custom-block"><summary>Byoukidere</summary><p>&#39;Byouki&#39; means illness, so these character(s) usually have some kind of physical disease, often fatal. They are mostly confined to a wheelchair or a hospital room. Generally, they are kind, caring and sweet in nature. Example: <a href="https://anidb.net/character/65811" target="_blank" rel="noreferrer">Miyazono Kaori from Shigatsu wa Kimi no Uso</a></p><figure><img src="'+g+'" alt="Miyazono Kaori" tabindex="0" loading="lazy"><figcaption>Miyazono Kaori</figcaption></figure></details><details class="details custom-block"><summary>Dandere</summary><p>Introverted character(s) that are shy, quiet, and asocial, they don&#39;t like to get involved in conflicts, but their affection is shown when they are alone or with a small group of people that they love and care. Example: <a href="https://anidb.net/character/117224" target="_blank" rel="noreferrer">Komi Shouko from Komi-san wa, Komyushou Desu.</a></p><figure><img src="'+p+'" alt="Komi Shouko" tabindex="0" loading="lazy"><figcaption>Komi Shouko</figcaption></figure></details><details class="details custom-block"><summary>Deredere</summary><p>&#39;Dere&#39; means love, so a deredere character is all about being lovey-dovey. These character(s) are sweet, kind and very affectionate towards the person they love, while also being generally friendly towards other characters. Example: <a href="https://anidb.net/character/73747" target="_blank" rel="noreferrer">Miia from Monster Musume</a></p><figure><img src="'+f+'" alt="Miia" tabindex="0" loading="lazy"><figcaption>Miia</figcaption></figure><table tabindex="0"><thead><tr><th>Terms</th><th>Meaning</th></tr></thead><tbody><tr><td>Oujodere</td><td>A lady-like (as denoted with &quot;oujo&quot;) version of deredere, that is more mature, calm and collected, typically they have a strict-nature on the outside, but are sweet on the inside. Prefer to share less about their crush, but is always pleased to help their crush and others, even in tough times. Example: <a href="https://anidb.net/character/8965" target="_blank" rel="noreferrer">Hanekawa Tsubasa from Bakemonogatari</a></td></tr><tr><td>Megadere</td><td>Think of it like mega-deredere. An extreme version of deredere, that has an abnormal-obsession with their crush and are not afraid to show it to their crush. Example: <a href="https://anidb.net/character/60162" target="_blank" rel="noreferrer">Tachibana Marika from Nisekoi</a></td></tr></tbody></table></details><details class="details custom-block"><summary>Goudere</summary><p>Over-achieving character(s) that are loyal to their masters, they view their masters as their savior and grace, so they do anything to their ability to keep them happy, and can also involve other characters as a means to contribute to their happiness, even though their master doesn&#39;t ask for that. Example: <a href="https://anidb.net/character/85812" target="_blank" rel="noreferrer">Tooru from Miss Kobayashi&#39;s Dragon Maid</a></p><figure><img src="'+y+'" alt="Tooru" tabindex="0" loading="lazy"><figcaption>Tooru</figcaption></figure></details><details class="details custom-block"><summary>Hajidere</summary><p>Character(s) that aren&#39;t nervous and embarrassed with others, but on the contrary, are when they are around their crush. Example: <a href="https://anidb.net/character/6071" target="_blank" rel="noreferrer">Boa Hancok from One Piece</a></p><figure><img src="'+b+'" alt="Boa Hancock" tabindex="0" loading="lazy"><figcaption>Boa Hancock</figcaption></figure></details><details class="details custom-block"><summary>Himedere/Oujidere/Kamidere</summary><table tabindex="0"><thead><tr><th>Terms</th><th>Meaning</th></tr></thead><tbody><tr><td>Himedere</td><td><strong>Hime</strong> means princess. It&#39;s a character who wishes to be treated like a princess or a queen by the person she loves, even if she is not actual royalty. Example: <a href="https://anidb.net/character/71284" target="_blank" rel="noreferrer">Nakiri Erina from Food Wars</a></td></tr><tr><td>Oujidere</td><td><strong>Ouji</strong> means prince. It&#39;s the male counterpart. Example: <a href="https://anidb.net/character/103" target="_blank" rel="noreferrer">Lelouch Lamperouge from Code Geass</a></td></tr><tr><td>Kamidere</td><td><strong>Kami</strong> means god. It&#39;s the same concept but taken to the extreme. Example: <a href="https://anidb.net/character/56259" target="_blank" rel="noreferrer">Kiryuuin Satsuki from Kill la Kill</a></td></tr></tbody></table></details><details class="details custom-block"><summary>Hinedere</summary><p>A Character(s) who has cynical world views, is cold-hearted, and is highly arrogant. They will get easily annoyed by other people, including their love interest at first. Example:<a href="https://anidb.net/character/51168" target="_blank" rel="noreferrer">Annie Leonhart from Attack on Titan</a></p><figure><img src="'+k+'" alt="Annie Leonhart" tabindex="0" loading="lazy"><figcaption>Annie Leonhart</figcaption></figure></details><details class="details custom-block"><summary>Hiyakasudere</summary><p>&#39;Hiyakasu&#39; means &#39;to tease&#39;. They are character(s) who likes to tease others and sometimes flirt with those they like. They will act really seductively in front of other people and will tease those that get embarrassed easily by their actions and words, possibly the love interest. Example: <a href="https://anidb.net/character/29" target="_blank" rel="noreferrer">Holo from Spice &amp; Wolf</a></p><figure><img src="'+w+'" alt="Holo" tabindex="0" loading="lazy"><figcaption>Holo</figcaption></figure></details><details class="details custom-block"><summary>Kanedere</summary><p>&#39;Kane&#39; means money. They are the anime equivalent of gold diggers. They often just care about how much money a person has, and nothing else. However, if they truly love someone, the habit can be broken. Example: <a href="https://anidb.net/character/4909" target="_blank" rel="noreferrer">Eva Heinemann from Monster</a></p><figure><img src="'+_+'" alt="Eva Heinemann" tabindex="0" loading="lazy"><figcaption>Eva Heinemann</figcaption></figure></details><details class="details custom-block"><summary>Mayadere</summary><p>A character(s) who used to be an antagonist or villain of the series but switched sides after falling in love or becoming fond of another character. Example: <a href="https://anidb.net/character/67551" target="_blank" rel="noreferrer">Esdeath from Akame ga Kill!</a></p><figure><img src="'+v+'" alt="Esdeath" tabindex="0" loading="lazy"><figcaption>Esdeath</figcaption></figure></details><details class="details custom-block"><summary>Nemuidere</summary><p>Character(s) that are sleepy - they sleep with or without an intent to do so, they will also try to get as much sleep as they can. Accompanied by those qualities they may also be lazy (which in this case they&#39;d be both a nemuidere and darudere), so they need motivation from others to do something, but they are usually very smart and artsy when they are too much of a sleepyhead. Example: <a href="https://anidb.net/character/108007" target="_blank" rel="noreferrer">Princess Syalis from Sleepy Princess in the Demon Castle</a></p><figure><img src="'+T+'" alt="Princess Syalis" tabindex="0" loading="lazy"><figcaption>Princess Syalis</figcaption></figure><table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Darudere</td><td>Similar to Nemuidere, but the difference is that where &#39;Nemui&#39; means sleepy, &#39;Daru&#39; means sluggish, they are very lazy and sluggish and unlike Nemuidere, they will only do things for people they care for and complain about it through the process of doing something, otherwise they are just dull and lazy to others. Example: <a href="https://anidb.net/character/75170" target="_blank" rel="noreferrer">Doma Umaru from Himouto! Umaru-chan</a></td></tr></tbody></table><blockquote><p>tl;dr: Nemui are sleepy characters, where as daru is lazy.</p></blockquote></details><details class="details custom-block"><summary>Nyandere</summary><p>Character(s) that are cat-loving fanatics, it could come in several variations: - They are a nekomimi themselves and just have a lot dere for cats - A regular human that is normal or show no emotion to others, but show their nyandere side when around cats, portrayed through showing imaginary cat ears and paws</p><p>Example: <a href="https://anidb.net/character/10841" target="_blank" rel="noreferrer">Mizuno Kaede from Nyan Koi!</a></p><figure><img src="'+x+'" alt="Mizuno Kaede" tabindex="0" loading="lazy"><figcaption>Mizuno Kaede</figcaption></figure></details><details class="details custom-block"><summary>Sadodere</summary><p>Character(s) that indulge in the manipulation of their crush&#39;s feelings and humiliating them. They are cold, violent and merciless to others, hence making them difficult to connect with others. They are also sadistic due to finding joy in inflicting pain on their crush. Example: <a href="https://anidb.net/anime/15622" target="_blank" rel="noreferrer">Nagatoro from Don`t Toy with Me, Miss Nagatoro</a></p><figure><img src="'+S+'" alt="Nagatoro" tabindex="0" loading="lazy"><figcaption>Nagatoro</figcaption></figure><table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Masodere</td><td>Character(s) that enjoy humiliating themselves, they compliment sadodere, so when combined together they are called Sadomasodere. Example: <a href="https://anidb.net/character/78021" target="_blank" rel="noreferrer">Darkness from Konosuba</a></td></tr></tbody></table></details><details class="details custom-block"><summary>Kekkondere</summary><p>Character(s) type that is obsessed with getting married right away, even if it&#39;s someone they just met, in other words love at first sight, Their insistence on getting married comes from wanting to spend the rest of their life with their love interest, it could get to the point where it becomes unhealthy, where they may turn into yanderes! Example: <a href="https://anidb.net/character/85802" target="_blank" rel="noreferrer">Fujinomiya Neko from Masamune-kun no Revenge</a></p><figure><img src="'+A+'" alt="Fujinomiya Neko" tabindex="0" loading="lazy"><figcaption>Fujinomiya Neko</figcaption></figure></details><details class="details custom-block"><summary>Kuudere</summary><p>Character(s) who are always calm and collected, especially in the most stressful of situations. They never panic and usually analyze and think through things til&#39; they solve the problem, it&#39;s rare for them to show affection or emotion to others in varying situations, however they could be hiding how they actually feel deep down. Example: <a href="https://anidb.net/character/79913" target="_blank" rel="noreferrer">Hishiro Chizuru from ReLife</a></p><figure><img src="'+E+'" alt="Hishiro Chizuru" tabindex="0" loading="lazy"><figcaption>Hishiro Chizuru</figcaption></figure><table tabindex="0"><thead><tr><th>Terms</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Shundere</td><td>Shundere is an extreme version kuudere version, where they are emotionless all the time due to sadness or depression. They choose to lock themselves away from others and fully hide their emotions more than the Kuudere. Example: <a href="https://anidb.net/character/56080" target="_blank" rel="noreferrer">Kuroki Tomoko from Watashi ga Motenai no wa Dou Kangaete mo Omaera ga Warui!</a></td></tr></tbody></table></details><details class="details custom-block"><summary>Tsundere</summary><p>Character(s) that show coldness and hostility on the outside, but have a heart of gold on the inside. Due to their prideful character, they tend to act mean to their love interest as a means to hide how they actually feel, because they don&#39;t want to hurt their pride, so they are not honest with themselves.</p><table tabindex="0"><thead><tr><th>Types</th><th class="compact">Meaning</th></tr></thead><tbody><tr><td>Type A: TsunTsun</td><td>Harsh by default. Only someone close will be able to bring out their soft side. They can&#39;t seem to control their temper and will lash out for any reason. Example: <a href="https://anidb.net/character/1009" target="_blank" rel="noreferrer">Taiga Aisaka from Toradora!</a></td></tr><tr><td>Type B: DereDere</td><td>Sweet by default. Their Tsun side comes out when they get embarrassed. Feelings of awkwardness around feelings and convos about love, overprotective of their partner, this can result in a lash out if their love interest or their relationship is threatened. Example: <a href="https://anidb.net/character/23271" target="_blank" rel="noreferrer">Makise Kurisu from Steins;Gate</a></td></tr></tbody></table><blockquote><p>There are several variations of the Tsundere types, which you can check out <a href="https://the-dere-types.fandom.com/wiki/Tsundere#Variations_and_Similar_Japanese_Archetypes" target="_blank" rel="noreferrer">here</a></p></blockquote></details><details class="details custom-block"><summary>Yandere</summary><p>Character(s) whose love and devotion are so strong that they become excessively obsessive and possessive. They are depicted as becoming crazy after falling in love, capable of doing any number of immoral things if it means being able to get closer with their love interest. Also, if they ever think they can&#39;t be with their love interest or that he is cheating on her, they&#39;d rather kill him and then herself rather than letting him be with someone else. Example: <a href="https://anidb.net/character/25306" target="_blank" rel="noreferrer">Yuno Gasai from Mirai Nikki</a></p><figure><img src="'+D+'" alt="Yuno Gasai from Mirai Nikki" tabindex="0" loading="lazy"><figcaption>Yuno Gasai from Mirai Nikki</figcaption></figure></details><details class="details custom-block"><summary>Yottadere</summary><p>Character(s) that can never stop having alcohol, they usually will always have alcohol with them, and they are usually sober when they are with someone they love. They can also have different personality types, from someone serious and passionate - to someone wild and brute. Example: <a href="https://anidb.net/character/90830" target="_blank" rel="noreferrer">Vanessa Enoteca from Black Clover</a></p><figure><img src="'+M+'" alt="Vanessa Enoteca" tabindex="0" loading="lazy"><figcaption>Vanessa Enoteca</figcaption></figure></details>',20)),a(l)])}const J=h(C,[["render",G]]);export{Y as __pageData,J as default};
